List<Widget> items(List<Zikr> _zikr, context) {
  List<Widget> myitems = [];
  for (var zikr in _zikr) {
    myitems.add(Container(
      child: Card(
        child: Stack(
          children: [
            zikr.title != '2'
                ? Container()
                : YoutubePlayer(
                    controller: YoutubePlayerController(
                      initialVideoId: zikr.contents,
                      flags: const YoutubePlayerFlags(
                        autoPlay: false,
                        mute: false,
                      ),
                    ),
                    showVideoProgressIndicator: true,
                    onReady: () {},
                  ),
            Container(
              width: MediaQuery.of(context).size.width - 50,
              margin: EdgeInsets.fromLTRB(10, 5, 5, 10),
              child: Text(
                zikr.contents,
                style: GoogleFonts.amiriQuran(fontSize: 22,fontWeight: FontWeight.bold),
                textAlign: TextAlign.right,
              ),
            )
          ],
        ),
      ),
    ));
  }

  return myitems;
}

class AZkar {
  final String title;
  List<Zikr> zikr = [];

  AZkar(this.title, this.zikr);
}

class Zikr {
  final String title;
  final String contents;
  final int count;

  Zikr(this.title, this.contents, this.count);
}

